import styled from 'styled-components'
import React from 'react';

import config from "../../config.json";
import { 
  Button
} from '../reusable/Other'


const Table = ({ columns, rows, values, onLoadMore, more, fetchMore, dataLength, setMore, action }) => {

  const loadMore = () => {
    fetchMore({
      variables: {
        offset: dataLength
      },
      updateQuery: (prev, { fetchMoreResult }) => {
        if (fetchMoreResult.me.transactions.length<1){
          setMore(false)
          return prev;
        } 
        let newObject1 = Object.assign({}, prev.me, {transactions: [...prev.me.transactions, ...fetchMoreResult.me.transactions]});
        let newObject2 = Object.assign({}, prev, {me: newObject1});
        console.log(newObject2)
        return newObject2
      }
    })
  }

  return(
    <TableWrapper>
      <Wrapper>
        <InnerWrapper>
          <Tr>
            {columns.map((item, i) => (
               <Th key={i} last={i===columns.length-1} first={i===0}>{item}</Th>
            ))}
          </Tr>
          {rows.map((row, i) => (
            <Tr key={i}>
              {values.map((keys, j) => {

                if(keys === "logo"){
                  let logoSrc = ""
                  if(row.logo === "PRON"){
                    logoSrc = config.pronIcon
                  } else if(row.logo === "PIPL"){
                    logoSrc = config.piplIcon
                  } else if(row.logo === "LOS"){
                    logoSrc = config.losIcon
                  } else if(row.logo === "EGROSH"){
                    logoSrc = config.groshIcon
                  } else {
                    logoSrc = row.logo
                  }
                  return <Td last={j===columns.length-1} first={j===0} key={j}>
                    <Img alt={logoSrc} src={logoSrc} />
                  </Td>
                }

                if(keys === "button"){
                  let text = ""
                  if(row.logo === "PRON"){
                    text = row.logo
                  } else if(row.logo === "PIPL"){
                    text = row.logo
                  } else if(row.logo === "LOS"){
                    text = row.logo
                  } else if(row.logo === "EGROSH"){
                    text = row.logo
                  } else {
                    text = row.logo
                  }
                  return <Td last={j===columns.length-1} first={j===0} key={j}>
                    <Button onClick={()=>action(text)} small={true}>{text}</Button>
                  </Td>
                }
                if(keys.length === 2){
                  return <Td last={j===columns.length-1} first={j===0} key={j}>{row[keys[0]][keys[1]]}</Td>
                } else if(keys.length === 3){
                  return <Td last={j===columns.length-1} first={j===0} key={j}>{row[keys[0]][keys[1]][keys[2]]}</Td>
                }
                return <Td last={j===columns.length-1} first={j===0} key={j}>{row[keys]}</Td>
              })}
            </Tr>
          ))}
        </InnerWrapper>
      </Wrapper>
      {rows.length===0 && <NoData>NO DATA FOUND</NoData>}
      {(more && rows[0]) && <Button small={true} onClick={loadMore}>Load more</Button>}
    </TableWrapper>
  )
}

export default Table


const TableWrapper = styled.div(props => ({
  overflowX:"auto",
  width:"100%",
}));
const Wrapper = styled.table(props => ({
  width:"100%",
  borderCollapse: "collapse",
}));
const InnerWrapper = styled.tbody(props => ({

}));
const Tr = styled.tr(props => ({
  borderBottom:"1px solid rgba(0, 0, 0, 0.07)",

}));
const Th = styled.th(props => ({
  textAlign:"left",
  borderTopLeftRadius:props.first ? 5 : 0, 
  borderBottomLeftRadius:props.first ? 5 : 0,
  borderTopRightRadius:props.last ? 5 : 0, 
  borderBottomRightRadius:props.last ? 5 : 0,
  background:"#D8D9DA",
  color:props.theme.colors.primary,
  paddingTop:8,
  paddingBottom:8,
  paddingLeft:30,
  fontSize:15,
  fontWeight:500,
}));
const Td = styled.td(props => ({
  paddingLeft:30,
  paddingTop:20,
  paddingBottom:20,
}));
const NoData = styled.div(props => ({
  display:"flex",
  marginTop:10,
  justifyContent:"center",
  borderRadius:10, 
  background:"#fff",
  boxShadow: "0px 8px 40px rgba(0, 0, 0, 0.03)",
  padding:30,
  fontSize:16,
  color:"#5B5B5B",
  opacity: 0.5,
}));
const Img = styled.img(props => ({
  height:30,
}));
